import React, {Component} from 'react';
import './App.css';

import TOC from "./component/TOC"
import ReadContent from "./component/Read_Content"
import CreateContent from "./component/Create_Content"
import UpdateContent from "./component/Update_Content"
import Subject from "./component/Subject"
import Control from "./component/Control"


class App extends Component{
  constructor(props){ 
    super(props); 

    this.count_contents_id=3;

    this.state={
      mode :"create", 
      welcome : {title:'welcome', desc:'hello react'},

      selected_content_id : 1,

      subject:{title:'WEB', sub:'world wide web !'},
      content:[
        {id:1, title:"HTML", desc:"HTML is HyperText "},
        {id:2, title:"CSS", desc:"Css is for design "},
        {id:3, title:"JS", desc:"javascript is for intrective "}
      ]
    }
  }
  getReadContent(){
    console.log('- getReadContent(선택한 콘텐츠 출력)');
    var i =0;
    while(i<this.state.content.length){
      var data = this.state.content[i];
      if(data.id === this.state.selected_content_id){
        return data;
        // _title=data.title;
        // _desc=data.desc;
        //break;
      }
      i++; 
    }
    //return data;
  }

  getContents(){
    console.log('- getContents()');
    var _title, _desc,_content = null;
    if(this.state.mode === 'welcome'){
      _title=this.state.welcome.title;
      _desc=this.state.welcome.desc;
      _content = <ReadContent title={_title} desc={_desc}></ReadContent>
    }else if(this.state.mode === 'read'){

      var _data = this.getReadContent(); // 함수로 바뀐 부분!의 리턴(data)값을 담기위해 변수를 선언한다.

      _content = <ReadContent title={_data._title} desc={_data._desc}></ReadContent>
    }else if(this.state.mode === 'create'){
      _content = <CreateContent onSubmit={function(_title, _desc){
        var _new_content = this.state.content.concat({
          id:this.count_contents_id, title:_title, desc:_desc
        });
        this.setState({
          content: _new_content
        });
      }.bind(this)}></CreateContent>
    }else if(this.state.mode === 'update'){

      var _data = this.getReadContent(); // 함수 재활용 

      _content = <UpdateContent onSubmit={function(_title, _desc){
        var _new_content = this.state.content.concat({
          id:this.count_contents_id, title:_data._title, desc:_data._desc
        });
        this.setState({
          content: _new_content
        });
      }.bind(this)}></UpdateContent>
    }
    return _content;
  }

  render(){
    console.log('APP render');
    return (
      <div className="App">
        <Subject 
          title={this.state.subject.title} 
          sub={this.state.subject.sub}

          onChangePage={function(){
            this.setState({
              mode:'welcome'
            });
          }.bind(this)}
          >
        </Subject>

         <TOC data={this.state.content}
          onChangePage={function(id){
            this.setState({
              mode : 'read',
              selected_content_id : Number(id)
            })
          }.bind(this)}
         ></TOC>

        <Control onChangeMode={function(_mode){
          this.setState({
            mode:_mode
          })
        }.bind(this)}></Control>

        {/* {_content} */}
        {this.getContents()}
      </div>
    );
  }
}
export default App;
